import axios from "axios";
import { io } from "socket.io-client";

const BASE_URL = "http://144.11.1.83:5000/api";
const SOCKET_URL = "http://144.11.1.83:5000";

// Initialize WebSocket
const socket = io(SOCKET_URL, {
  reconnectionAttempts: 5,
  timeout: 5000,
});

// Generic API Request
const apiRequest = async (method, endpoint, data = null) => {
  try {
    const response = await axios({
      method,
      url: `${BASE_URL}${endpoint}`,
      data,
    });
    return response.data;
  } catch (error) {
    console.error(`âŒ API Error [${method.toUpperCase()} ${endpoint}]:`, error.response?.data || error);
    return { error: error.response?.data || "Request failed" };
  }
};

// API Exports
export const fetchActiveRide = (userId) =>
  apiRequest("get", `/tracking/ride-status/${userId}`);

export const bookRide = async (userId, fromLocation, toLocation, vehicleType, rideType) => {
  if (!userId || isNaN(userId)) return { error: "Invalid User ID" };

  const response = await apiRequest("post", "/rides/book-ride", {
    userId,
    fromLocation,
    toLocation,
    vehicleType,
  });

  if (response?.rideId) {
    socket.emit("newRideRequest", { rideId: response.rideId });
  }

  return response;
};

export const acceptRide = async (driverId, rideId) => {
  if (!driverId || !rideId) return { error: "Invalid parameters" };

  const response = await apiRequest("post", "/tracking/accept-ride", {
    driverId,
    rideId,
  });

  if (response.success) {
    socket.emit("acceptRide", { rideId, driverId });
  }

  return response;
};

export const startRide = (rideId) =>
  apiRequest("post", "/tracking/start-ride", { rideId });

export const markPickTime = (rideId) =>
  apiRequest("post", "/tracking/mark-pick-time", { rideId });

export const completeRide = async (rideId) => {
  console.log("ðŸ“¡ Completing ride:", rideId);
  const response = await apiRequest("post", "/tracking/complete-ride", { rideId });

  if (response.success) {
    socket.emit("rideCompleted", {
      rideId,
      driverId: response.ride?.driverId || "unknown",
      endTime: response.ride?.completedAt,
    });
    console.log("âœ… Ride completion successful");
  } else {
    console.error("âŒ Completion failed:", response.error);
  }

  return response;
};

export const fetchMaintenanceRequests = () =>
  apiRequest("get", "/maintenance/issues");

export const reportMaintenance = (forkliftId, issue) =>
  apiRequest("post", "/maintenance/report-maintenance", { forkliftId, issue });

export const resolveMaintenance = (requestId) =>
  apiRequest("post", "/maintenance/resolve-maintenance", { requestId });

export const fetchRideReports = () =>
  apiRequest("get", "/reports/rides");

export const markHome = (driverId) =>
  apiRequest("post", "/drivers/mark-home", { driverId });

// WebSocket Listeners
socket.on("newRideRequest", (rideData) =>
  console.log("ðŸ“Œ New ride request received:", rideData)
);

socket.on("rideAccepted", ({ rideId, driverId }) =>
  console.log(`âœ… Ride ${rideId} accepted by Driver ${driverId}`)
);

socket.on("rideCompleted", ({ rideId, driverId, endTime }) =>
  console.log(`ðŸ Ride ${rideId} completed by Driver ${driverId} at ${endTime}`)
);

socket.on("driverStatusUpdated", ({ driverId, status }) =>
  console.log(`ðŸ”„ Driver ${driverId} status updated to ${status}`)
);
