import React, { useState, useEffect, useRef } from "react";
import { useNavigate } from "react-router-dom";
import { useSpring, animated } from "@react-spring/web";
import Confetti from "react-confetti";
import { bookRide } from "../Services/api";
import { io } from "socket.io-client";
import "./SpecialTaskRide.css";

const SOCKET_URL = "http://144.11.1.83:5000";

const hours = Array.from({ length: 2 }, (_, i) => i); // 0 to 1 hour
const minutes = Array.from({ length: 60 }, (_, i) => i); // 0 to 59 minutes

const SpecialTaskRide = ({ user }) => {
  const navigate = useNavigate();
  const [selectedHour, setSelectedHour] = useState(0);
  const [selectedMinute, setSelectedMinute] = useState(0);
  const [showConfirmation, setShowConfirmation] = useState(false);
  const [showConfetti, setShowConfetti] = useState(false);
  const [bookingSuccess, setBookingSuccess] = useState(false);
  const [fromLocation, setFromLocation] = useState("");
  const [toLocation, setToLocation] = useState("");
  const [estimatedTime, setEstimatedTime] = useState("");
  const [rideStatus, setRideStatus] = useState("");
  const [rideId, setRideId] = useState(null);
  const [socket, setSocket] = useState(null);
  const [windowSize, setWindowSize] = useState({
    width: window.innerWidth,
    height: window.innerHeight,
  });
  const [timeError, setTimeError] = useState("");
  const minuteScrollRef = useRef(null);
  const hourScrollRef = useRef(null);

  const confirmationSpring = useSpring({
    opacity: showConfirmation ? 1 : 0,
    transform: showConfirmation ? "translateY(0px)" : "translateY(-20px)",
    config: { tension: 300, friction: 20 }
  });

  const masterData = [
    { from: "SSD", to: "Boiler", time: 4 },
    { from: "SSD", to: "RMS", time: 10 },
    { from: "SSD", to: "Front gate", time: 5 },
    { from: "SSD", to: "Back gate", time: 8 },
    { from: "SSD", to: "Utility", time: 5 },
    { from: "RMS", to: "Front gate", time: 7 },
    { from: "RMS", to: "Back gate", time: 4 },
    { from: "RMS", to: "Boiler", time: 5 },
    { from: "RMS", to: "Utility", time: 4 },
  ];

  const uniqueFromLocations = [...new Set(masterData.map((data) => data.from))];
  const uniqueToLocations = [...new Set(masterData.map((data) => data.to))];

  useEffect(() => {
    const handleResize = () => {
      setWindowSize({
        width: window.innerWidth,
        height: window.innerHeight,
      });
    };

    window.addEventListener("resize", handleResize);
    return () => window.removeEventListener("resize", handleResize);
  }, []);

  useEffect(() => {
    if (fromLocation && toLocation) {
      const route = masterData.find(
        (route) => route.from === fromLocation && route.to === toLocation
      );
      if (route) {
        setEstimatedTime(route.time);
      } else {
        setEstimatedTime(null);
      }
    } else {
      setEstimatedTime(null);
    }
  }, [fromLocation, toLocation]);

  useEffect(() => {
    const newSocket = io(SOCKET_URL, { autoConnect: true });
    setSocket(newSocket);

    newSocket.on("connect", () => {
      console.log("‚úÖ Connected to SpecialTaskRide Socket.IO server");
    });

    newSocket.on("disconnect", () => {
      console.log("‚ùå Disconnected from SpecialTaskRide Socket.IO server");
    });

    newSocket.onAny((event, ...args) => {
      console.log(`üì° Received event: ${event}`, args);
    });

    newSocket.on("rideAccepted", (data) => {
      const { rideId: acceptedRideId } = data;
      console.log("Received rideAccepted event with rideId:", acceptedRideId);

      if (acceptedRideId === rideId) {
        setRideStatus("accepted");
        setShowConfetti(true);
        setBookingSuccess(true);
        
        setTimeout(() => {
          navigate("/requester");
        }, 5000);
      }
    });

    return () => {
      newSocket.disconnect();
      console.log("‚ùå Disconnected from Socket.IO server");
    };
  }, [rideId]);

  useEffect(() => {
    const handleScroll = (ref, setter, items) => {
      const index = Math.round(ref.current.scrollTop / 40);
      if (index >= 0 && index < items.length) {
        setter(items[index]);
      }
    };

    const minuteScroll = minuteScrollRef.current;
    const hourScroll = hourScrollRef.current;

    const handleMinuteScroll = () => handleScroll(minuteScrollRef, setSelectedMinute, minutes);
    const handleHourScroll = () => handleScroll(hourScrollRef, setSelectedHour, hours);

    if (minuteScroll) minuteScroll.addEventListener("scroll", handleMinuteScroll);
    if (hourScroll) hourScroll.addEventListener("scroll", handleHourScroll);

    return () => {
      if (minuteScroll) minuteScroll.removeEventListener("scroll", handleMinuteScroll);
      if (hourScroll) hourScroll.removeEventListener("scroll", handleHourScroll);
    };
  }, []);

  const handleTimeClick = (value, type) => {
    if (type === 'hour') {
      setSelectedHour(value);
      if (hourScrollRef.current) {
        hourScrollRef.current.scrollTo({
          top: value * 40,
          behavior: 'smooth'
        });
      }
    } else {
      setSelectedMinute(value);
      if (minuteScrollRef.current) {
        minuteScrollRef.current.scrollTo({
          top: value * 40,
          behavior: 'smooth'
        });
      }
    }
  };

  const handleConfirm = () => {
    if (!fromLocation || !toLocation) {
      alert("‚ùå Please select 'From' and 'To' locations.");
      return;
    }

    // Check if time is 00h 00m
    if (selectedHour === 0 && selectedMinute === 0) {
      setTimeError("Please select a time greater than 00h 00m");
      return;
    }

    // Check if time is less than 1 hour
    if (selectedHour === 0 && selectedMinute < 60) {
      setTimeError("For rides less than 1 hour, please use the Fixed Ride option");
      return;
    }

    setTimeError("");
    setShowConfirmation(true);
  };

  const handleYes = async () => {
    let userId = user && user.id ? Number(user.id) : NaN;

    if (isNaN(userId)) {
      console.warn("‚ö†Ô∏è User ID is invalid, using a temporary ID.");
      userId = Math.floor(Math.random() * 9000) + 1000;
    }

    setShowConfirmation(false);
    setRideStatus("requested");

    try {
      const response = await bookRide(userId, fromLocation, toLocation, "Forklift", "Special Task");

      if (response.rideId) {
        console.log("‚úÖ Special Task Ride booked successfully! Ride ID:", response.rideId);
        setRideId(response.rideId);
        socket.emit("rideRequested", { rideId: response.rideId });
      } else {
        console.error("‚ùå Failed to book special task ride.");
        alert("‚ùå Failed to book ride.");
        setRideStatus("");
      }
    } catch (error) {
      console.error("‚ùå API Error:", error);
      alert("‚ùå Error booking ride.");
      setRideStatus("");
    }
  };

  const handleNo = () => {
    setShowConfirmation(false);
  };

  return (
    <div className="special-task-container">
      {showConfetti && (
        <Confetti 
          width={windowSize.width}
          height={windowSize.height}
          numberOfPieces={400} 
          gravity={0.2}
        />
      )}

      <div className="special-task-header">
        <h2>Schedule Special Task Ride</h2>
        <p className="subtitle">Book a ride for tasks requiring more than 1 hour</p>
      </div>

      <div className="form-container">
        {showConfirmation && (
          <animated.div className="confirmation-overlay" style={confirmationSpring}>
            <div className="confirmation-box">
              <h3>Confirm Special Task Ride</h3>
              <p>Are you sure you want to book this ride for {selectedHour}h {selectedMinute}m?</p>
              {timeError && <div className="error-message">{timeError}</div>}
              <div className="confirmation-buttons">
                <button className="yes-button" onClick={handleYes}>Confirm</button>
                <button className="no-button" onClick={handleNo}>Cancel</button>
              </div>
            </div>
          </animated.div>
        )}

        <div className="input-group">
          <label>From Location:</label>
          <div className="custom-select">
            <select 
              value={fromLocation} 
              onChange={(e) => setFromLocation(e.target.value)}
              className={fromLocation ? "selected" : ""}
            >
              <option value="" disabled>Select From</option>
              {uniqueFromLocations.map((loc, index) => (
                <option key={index} value={loc}>{loc}</option>
              ))}
            </select>
            <span className="dropdown-arrow">&#9660;</span>
          </div>
        </div>

        <div className="input-group">
          <label>To Location:</label>
          <div className="custom-select">
            <select 
              value={toLocation} 
              onChange={(e) => setToLocation(e.target.value)}
              className={toLocation ? "selected" : ""}
            >
              <option value="" disabled>Select To</option>
              {uniqueToLocations.map((loc, index) => (
                <option key={index} value={loc}>{loc}</option>
              ))}
            </select>
            <span className="dropdown-arrow">&#9660;</span>
          </div>
        </div>

        {estimatedTime && (
          <div className="estimated-time">
            <p>
              Estimated travel time: <span className="highlight">{estimatedTime} minutes</span>
            </p>
          </div>
        )}

        <div className="time-selection-container">
          <h3 className="time-selection-title">Select Duration</h3>
          <p className="time-instruction">(Minimum 1 hour required)</p>
          
          <div className="time-selection">
            <div className="time-column">
              <h4>Hours</h4>
              <div ref={hourScrollRef} className="time-scroll">
                {hours.map((hour) => (
                  <div
                    key={hour}
                    className={`time-item ${hour === selectedHour ? "selected" : ""}`}
                    onClick={() => handleTimeClick(hour, 'hour')}
                  >
                    {hour.toString().padStart(2, "0")}
                  </div>
                ))}
              </div>
            </div>

            <div className="time-separator">:</div>

            <div className="time-column">
              <h4>Minutes</h4>
              <div ref={minuteScrollRef} className="time-scroll">
                {minutes.map((minute) => (
                  <div
                    key={minute}
                    className={`time-item ${minute === selectedMinute ? "selected" : ""}`}
                    onClick={() => handleTimeClick(minute, 'minute')}
                  >
                    {minute.toString().padStart(2, "0")}
                  </div>
                ))}
              </div>
            </div>
          </div>

          <div className="selected-time-display">
            <span className="time-value">{selectedHour.toString().padStart(2, "0")}</span>
            <span className="time-label">h</span>
            <span className="time-value">{selectedMinute.toString().padStart(2, "0")}</span>
            <span className="time-label">m</span>
          </div>
        </div>

        {timeError && (
          <div className="error-message">
            {timeError}
          </div>
        )}

        {rideStatus === "requested" && (
          <div className="ride-status">
            <div className="loading-spinner"></div>
            <p>Requesting your special task ride...</p>
          </div>
        )}

        {rideStatus === "accepted" && (
          <div className="ride-status success">
            <svg
              xmlns="http://www.w3.org/2000/svg"
              width="24"
              height="24"
              viewBox="0 0 24 24"
              fill="none"
              stroke="currentColor"
              strokeWidth="2"
              strokeLinecap="round"
              strokeLinejoin="round"
            >
              <path d="M22 11.08V12a10 10 0 1 1-5.93-9.14"></path>
              <polyline points="22 4 12 14.01 9 11.01"></polyline>
            </svg>
            <p>Your special task ride has been accepted!</p>
            <p className="redirect-notice">Driver will arrive at the scheduled time</p>
          </div>
        )}

        {!rideStatus && !showConfirmation && (
          <button 
            className="confirm-button" 
            onClick={handleConfirm}
            disabled={!fromLocation || !toLocation}
          >
            Schedule Ride
          </button>
        )}
      </div>
    </div>
  );
};

export default SpecialTaskRide;
