import React, { useState, useEffect } from "react";
import { io } from "socket.io-client";
import "./DriverDashboard.css";
import { acceptRide, completeRide } from "../Services/api";

const SOCKET_URL = process.env.REACT_APP_SOCKET_URL || "http://localhost:5000";

const DriverDashboard = ({ driver }) => {
  const [ongoingRide, setOngoingRide] = useState(null);
  const [newRideRequests, setNewRideRequests] = useState([]);
  const [loading, setLoading] = useState(false);
  const [progress, setProgress] = useState(0);
  const [showEndRideConfirmation, setShowEndRideConfirmation] = useState(false);
  const [rideEndedMessage, setRideEndedMessage] = useState("");
  const [rideAcceptedMessage, setRideAcceptedMessage] = useState("");
  const [socket, setSocket] = useState(null);
  const [isConnected, setIsConnected] = useState(false);

  // Initialize Socket.IO connection
  useEffect(() => {
    const newSocket = io(SOCKET_URL, {
      autoConnect: true,
      reconnection: true,
      reconnectionAttempts: 5,
      reconnectionDelay: 1000,
    });

    newSocket.on('connect', () => {
      console.log('Socket connected');
      setIsConnected(true);
    });

    newSocket.on('disconnect', () => {
      console.log('Socket disconnected');
      setIsConnected(false);
    });

    newSocket.on('connect_error', (err) => {
      console.error('Socket connection error:', err);
    });

    setSocket(newSocket);

    return () => {
      newSocket.off('connect');
      newSocket.off('disconnect');
      newSocket.off('connect_error');
      newSocket.disconnect();
    };
  }, []);

  // Listen for new ride requests
  useEffect(() => {
    if (!socket) return;

    const handleNewRideRequest = () => {
      fetchNewRideRequests();
    };

    socket.on('rideRequested', handleNewRideRequest);

    return () => {
      socket.off('rideRequested', handleNewRideRequest);
    };
  }, [socket]);

  // Fetch new ride requests
  const fetchNewRideRequests = async () => {
    try {
      const response = await fetch(`${SOCKET_URL}/api/drivers/available-rides`);
      if (!response.ok) {
        throw new Error("Failed to fetch driver data");
      }
      const data = await response.json();
      const rides = data.rides.rows || [];
      setNewRideRequests(rides);
    } catch (error) {
      console.error("‚ùå Error fetching driver data:", error);
    }
  };

  // Handle accepting a ride
  const handleAcceptRide = async (rideId) => {
    if (loading) return;
    setLoading(true);

    try {
      const response = await acceptRide(driver.id, rideId);
      setRideAcceptedMessage("‚úÖ Ride accepted successfully!");
      
      const acceptedRide = newRideRequests.find((ride) => ride.ID === rideId);
      setOngoingRide(acceptedRide);
      setNewRideRequests((prev) => prev.filter((ride) => ride.ID !== rideId));
      
      if (socket) {
        socket.emit("rideAccepted", { 
          rideId,
          driverId: driver.id 
        });
      }
    } catch (error) {
      setRideAcceptedMessage("‚ùå Failed to accept ride.");
      console.error("‚ùå Error accepting ride:", error);
    }
    setLoading(false);
  };

  // Handle ending a ride
  const handleEndRide = async () => {
    if (!ongoingRide || !socket) return;
  
    try {
      const response = await completeRide(ongoingRide.ID);
      setRideEndedMessage("‚úÖ Ride ended successfully!");
  
      socket.emit("rideEnded", { 
        rideId: ongoingRide.ID,
        driverId: driver.id 
      });
  
      setTimeout(() => {
        setOngoingRide(null);
        setProgress(0);
        setRideEndedMessage("");
        fetchNewRideRequests();
      }, 2000);
    } catch (error) {
      setRideEndedMessage("‚ùå Failed to end ride.");
      console.error("‚ùå Error ending ride:", error);
    }
  };

  // Rest of your component remains the same...
  // [Keep all your existing JSX and other useEffect hooks]

  return (
    <div className="driver-dashboard">
      {/* Connection status indicator */}
      <div className={`connection-status ${isConnected ? 'connected' : 'disconnected'}`}>
        {isConnected ? 'üü¢ Connected' : 'üî¥ Disconnected'}
      </div>
      
      {/* Rest of your existing JSX */}
      {/* ... */}
    </div>
  );
};

export default DriverDashboard;
