import React, { useState, useEffect } from "react";
import { io } from "socket.io-client";
import { useNavigate } from "react-router-dom";
import "./DriverDashboard.css";
import { acceptRide, completeRide } from "../Services/api";

const SOCKET_URL = "http://144.11.1.83:5000";

const DriverDashboard = ({ driver }) => {
  const [ongoingRide, setOngoingRide] = useState(null);
  const [newRideRequests, setNewRideRequests] = useState([]);
  const [loading, setLoading] = useState(false);
  const [progress, setProgress] = useState(0);
  const [showEndRideConfirmation, setShowEndRideConfirmation] = useState(false);
  const [rideEndedMessage, setRideEndedMessage] = useState("");
  const [rideAcceptedMessage, setRideAcceptedMessage] = useState("");
  const [socket, setSocket] = useState(null);
  const navigate = useNavigate();

  // Initialize Socket.IO connection
  useEffect(() => {
    const newSocket = io(SOCKET_URL, { autoConnect: true });
    setSocket(newSocket);

    newSocket.on("connect", () => {
      console.log("âœ… Connected to DriverDashboard Socket.IO server");
    });

    newSocket.on("disconnect", () => {
      console.log("âŒ Disconnected from DriverDashboard Socket.IO server");
    });

    newSocket.on("newRideRequest", (newRide) => {
      console.log("ðŸ†• New ride received:", newRide);
      setNewRideRequests((prevRides) => [newRide, ...prevRides]);
    });

    return () => {
      newSocket.disconnect();
    };
  }, []);

  // Fetch new ride requests
  const fetchNewRideRequests = async () => {
    try {
      const response = await fetch(`${SOCKET_URL}/api/drivers/available-rides`);
      if (!response.ok) throw new Error("Failed to fetch driver data");
      const data = await response.json();
      setNewRideRequests(data.rides.rows || []);
    } catch (error) {
      console.error("âŒ Error fetching driver data:", error);
    }
  };

  // Handle accepting a ride
  const handleAcceptRide = async (rideId) => {
    if (loading) return;
    setLoading(true);
    
    try {
      const acceptedRide = newRideRequests.find((ride) => ride.ID === rideId);
      if (!acceptedRide) throw new Error("Ride not found");
      
      await acceptRide(rideId, driver.id);
      setOngoingRide(acceptedRide);
      setNewRideRequests((prev) => prev.filter((ride) => ride.ID !== rideId));
      setRideAcceptedMessage("âœ… Ride accepted successfully!");
      
      if (socket) {
        socket.emit("rideAccepted", { rideId });
      }
    } catch (error) {
      setRideAcceptedMessage("âŒ Failed to accept ride.");
      console.error("âŒ Error accepting ride:", error);
    } finally {
      setLoading(false);
    }
  };

  // Handle ending a ride
  const handleEndRide = async () => {
    if (!ongoingRide || !socket) return;
    
    try {
      await completeRide(ongoingRide.ID);
      setRideEndedMessage("âœ… Ride ended successfully!");
      socket.emit("rideEnded", { rideId: ongoingRide.ID });

      setTimeout(() => {
        setOngoingRide(null);
        setProgress(0);
        setRideEndedMessage("");
        fetchNewRideRequests();
        navigate("/driver");
      }, 2000);
    } catch (error) {
      setRideEndedMessage("âŒ Failed to end ride.");
      console.error("âŒ Error ending ride:", error);
    }
  };

  // Simulate ride progress
  useEffect(() => {
    if (ongoingRide) {
      const interval = setInterval(() => {
        setProgress((prev) => (prev >= 100 ? 100 : prev + 10));
      }, 1000);
      return () => clearInterval(interval);
    }
  }, [ongoingRide]);

  // Fetch new ride requests on mount
  useEffect(() => {
    fetchNewRideRequests();
  }, []);

  return (
    <div className="driver-dashboard">
      <header className="dashboard-header">
        <h2>Driver Dashboard</h2>
        {driver && <div className="driver-info">Welcome, {driver.name}</div>}
      </header>

      {/* Ongoing Ride Section */}
      {ongoingRide ? (
        <div className="ongoing-ride-card">
          <div className="ride-header">
            <h3>Ongoing Ride</h3>
            <span className={`ride-type-badge ${ongoingRide.SPECIAL_TASK ? 'special' : 'regular'}`}>
              {ongoingRide.SPECIAL_TASK ? 'Special Task' : 'Regular Ride'}
            </span>
          </div>
          
          <div className="ride-details">
            <p><strong>From:</strong> {ongoingRide.FROM_LOCATION}</p>
            <p><strong>To:</strong> {ongoingRide.TO_LOCATION}</p>
          </div>

          <div className="progress-container">
            <div className="progress-bar">
              <div className="progress-fill" style={{ width: `${progress}%` }}></div>
              <div className="progress-car" style={{ left: `${progress}%` }}>ðŸš—</div>
            </div>
            <div className="progress-labels">
              <span>Pickup</span>
              <span>Destination</span>
            </div>
          </div>

          {showEndRideConfirmation ? (
            <div className="confirmation-modal">
              <p>Are you sure you want to end this ride?</p>
              <div className="confirmation-actions">
                <button className="confirm-btn" onClick={handleEndRide}>Yes, End Ride</button>
                <button className="cancel-btn" onClick={() => setShowEndRideConfirmation(false)}>
                  Cancel
                </button>
              </div>
            </div>
          ) : (
            <button 
              className="end-ride-btn"
              onClick={() => setShowEndRideConfirmation(true)}
            >
              End Ride
            </button>
          )}

          {rideEndedMessage && (
            <div className="status-message success">
              {rideEndedMessage}
            </div>
          )}
        </div>
      ) : (
        <div className="no-rides-card">
          <p>No ongoing ride at the moment</p>
        </div>
      )}

      {rideAcceptedMessage && (
        <div className="status-message success">
          {rideAcceptedMessage}
        </div>
      )}

      {/* Available Rides Section */}
      {!ongoingRide && (
        <div className="available-rides-section">
          <h3 className="section-title">Available Rides</h3>
          {newRideRequests.length > 0 ? (
            <table className="rides-table">
              <thead>
                <tr>
                  <th>From</th>
                  <th>To</th>
                  <th>Type</th>
                  <th>Action</th>
                </tr>
              </thead>
              <tbody>
                {newRideRequests.map((ride) => (
                  <tr key={ride.ID}>
                    <td>{ride.FROM_LOCATION}</td>
                    <td>{ride.TO_LOCATION}</td>
                    <td>
                      <span className={`ride-type-badge ${ride.SPECIAL_TASK ? 'special' : 'regular'}`}>
                        {ride.SPECIAL_TASK ? 'Special Task' : 'Regular Ride'}
                      </span>
                    </td>
                    <td>
                      <button
                        className="accept-btn"
                        onClick={() => handleAcceptRide(ride.ID)}
                        disabled={loading}
                      >
                        {loading ? 'Processing...' : 'Accept Ride'}
                      </button>
                    </td>
                  </tr>
                ))}
              </tbody>
            </table>
          ) : (
            <div className="no-rides-message">
              No rides available at the moment
            </div>
          )}
        </div>
      )}
    </div>
  );
};

export default DriverDashboard;
