import React, { useState, useEffect } from "react";
import "./MaintenanceDashboard.css";
import { fetchMaintenanceRequests, resolveMaintenance } from "../Services/api";

const MaintenanceDashboard = () => {
  const [maintenanceRequests, setMaintenanceRequests] = useState([]);
  const [loading, setLoading] = useState(false);

  useEffect(() => {
    const loadRequests = async () => {
      try {
        setMaintenanceRequests(await fetchMaintenanceRequests());
      } catch (error) {
        console.error("Error loading maintenance requests:", error);
      }
    };

    loadRequests();
  }, []);

  const handleResolveMaintenance = async (requestId) => {
    setLoading(true);
    try {
      await resolveMaintenance(requestId);
      setMaintenanceRequests(await fetchMaintenanceRequests());
    } catch (error) {
      console.error("Error resolving maintenance request:", error);
    }
    setLoading(false);
  };

  return (
    <div className="maintenance-dashboard">
      <h2>Maintenance Dashboard</h2>
      <ul>
        {maintenanceRequests.length === 0 ? (
          <p>No pending maintenance requests.</p>
        ) : (
          maintenanceRequests.map((request) => (
            <li key={request.id}>
              {request.forkliftId} - {request.issue}
              <button onClick={() => handleResolveMaintenance(request.id)} disabled={loading}>
                {loading ? "Processing..." : "Resolve"}
              </button>
            </li>
          ))
        )}
      </ul>
    </div>
  );
};

export default MaintenanceDashboard;
