import React, { useState, useEffect } from "react";
import { io } from "socket.io-client";
import { useNavigate } from "react-router-dom";
import "./DriverDashboard.css";
import { acceptRide, completeRide } from "../Services/api";

const SOCKET_URL = "http://144.11.1.83:5000";

const DriverDashboard = ({ driver }) => {
  const [ongoingRide, setOngoingRide] = useState(null);
  const [newRideRequests, setNewRideRequests] = useState([]);
  const [loading, setLoading] = useState(false);
  const [progress, setProgress] = useState(0);
  const [showEndRideConfirmation, setShowEndRideConfirmation] = useState(false);
  const [rideEndedMessage, setRideEndedMessage] = useState("");
  const [rideAcceptedMessage, setRideAcceptedMessage] = useState("");
  const [socket, setSocket] = useState(null);
  const [currentPage, setCurrentPage] = useState(1);
  const navigate = useNavigate();

  const ridesPerPage = 10;

  useEffect(() => {
    const newSocket = io(SOCKET_URL, { autoConnect: true });
    setSocket(newSocket);

    newSocket.on("connect", () => {
      console.log("✅ Connected to DriverDashboard Socket.IO server");
    });

    newSocket.on("disconnect", () => {
      console.log("❌ Disconnected from DriverDashboard Socket.IO server");
    });

    newSocket.on("newRideRequest", (newRide) => {
      console.log("🆕 New ride received:", newRide);
      setNewRideRequests((prevRides) => [newRide, ...prevRides]);
    });

    return () => {
      newSocket.disconnect();
    };
  }, []);

  const fetchNewRideRequests = async () => {
    try {
      const response = await fetch(`${SOCKET_URL}/api/drivers/available-rides`);
      if (!response.ok) throw new Error("Failed to fetch driver data");
      const data = await response.json();
      setNewRideRequests(data.rides.rows || []);
      setCurrentPage(1); // Reset to first page when new rides load
    } catch (error) {
      console.error("❌ Error fetching driver data:", error);
    }
  };

  const handleAcceptRide = async (rideId) => {
    if (loading) return;
    setLoading(true);
    
    try {
      const acceptedRide = newRideRequests.find((ride) => ride.ID === rideId);
      if (!acceptedRide) throw new Error("Ride not found");
      
      await acceptRide(rideId, driver.id);
      setOngoingRide(acceptedRide);
      setNewRideRequests((prev) => prev.filter((ride) => ride.ID !== rideId));
      setRideAcceptedMessage("✅ Ride accepted successfully!");
      
      if (socket) {
        socket.emit("rideAccepted", { rideId });
      }
    } catch (error) {
      setRideAcceptedMessage("❌ Failed to accept ride.");
      console.error("❌ Error accepting ride:", error);
    } finally {
      setLoading(false);
    }
  };

  const handleEndRide = async () => {
    if (!ongoingRide || !socket) return;
    
    try {
      const response = await completeRide(ongoingRide.ID);
      if (!response.success) throw new Error("Failed to end ride");
      
      setRideEndedMessage("✅ Ride ended successfully!");
      socket.emit("rideEnded", { rideId: ongoingRide.ID });

      setTimeout(() => {
        setOngoingRide(null);
        setProgress(0);
        setRideEndedMessage("");
        fetchNewRideRequests();
        navigate("/driver");
      }, 2000);
    } catch (error) {
      console.error("❌ Error ending ride:", error);
      setRideEndedMessage("❌ Failed to end ride. Please try again.");
    }
  };

  useEffect(() => {
    if (ongoingRide) {
      const interval = setInterval(() => {
        setProgress((prev) => (prev >= 100 ? 100 : prev + 10));
      }, 1000);
      return () => clearInterval(interval);
    }
  }, [ongoingRide]);

  useEffect(() => {
    fetchNewRideRequests();
  }, []);

  const top3ClosestRides = newRideRequests.slice(0, 3);
  const otherAvailableRides = newRideRequests.slice(3);

  // Pagination logic
  const indexOfLastRide = currentPage * ridesPerPage;
  const indexOfFirstRide = indexOfLastRide - ridesPerPage;
  const currentRides = otherAvailableRides.slice(indexOfFirstRide, indexOfLastRide);
  const totalPages = Math.ceil(otherAvailableRides.length / ridesPerPage);

  const nextPage = () => {
    if (currentPage < totalPages) {
      setCurrentPage(currentPage + 1);
    }
  };

  const prevPage = () => {
    if (currentPage > 1) {
      setCurrentPage(currentPage - 1);
    }
  };

  return (
    <div className="driver-dashboard">
      <header className="dashboard-header">
        <h2>Driver Dashboard</h2>
        {driver && <div className="driver-info">Welcome, {driver.name}</div>}
      </header>

      {ongoingRide ? (
        <div className="ongoing-ride-card">
          <div className="ride-header">
            <h3>Ongoing Ride</h3>
            <span className={`ride-type-badge ${ongoingRide.IS_SPECIAL_TASK ? 'special' : 'regular'}`}>
              {ongoingRide.IS_SPECIAL_TASK ? 'Special Task Ride' : 'Regular Ride'}
            </span>
          </div>
          
          <div className="ride-details">
            <p><strong>From:</strong> {ongoingRide.FROM_LOCATION}</p>
            <p><strong>To:</strong> {ongoingRide.TO_LOCATION}</p>
          </div>

          <div className="progress-container">
            <div className="progress-bar">
              <div className="progress-fill" style={{ width: `${progress}%` }}></div>
              <div className="progress-car" style={{ left: `${progress}%` }}>🚗</div>
            </div>
            <div className="progress-labels">
              <span>Pickup</span>
              <span>Destination</span>
            </div>
          </div>

          {showEndRideConfirmation ? (
            <div className="confirmation-modal">
              <p>Are you sure you want to end this ride?</p>
              <div className="confirmation-actions">
                <button className="confirm-btn" onClick={handleEndRide}>Yes, End Ride</button>
                <button className="cancel-btn" onClick={() => setShowEndRideConfirmation(false)}>
                  Cancel
                </button>
              </div>
            </div>
          ) : (
            <button 
              className="end-ride-btn"
              onClick={() => setShowEndRideConfirmation(true)}
            >
              End Ride
            </button>
          )}

          {rideEndedMessage && (
            <div className="status-message success">
              {rideEndedMessage}
            </div>
          )}
        </div>
      ) : (
        <div className="no-rides-card">
          <p>No ongoing ride at the moment</p>
        </div>
      )}

      {rideAcceptedMessage && (
        <div className="status-message success">
          {rideAcceptedMessage}
        </div>
      )}

      {!ongoingRide && (
        <div className="available-rides-section">
          <h3 className="section-title">Top 3 Closest Rides</h3>
          {top3ClosestRides.length > 0 ? (
            <div className="table-container">
              <table className="rides-table">
                <thead>
                  <tr>
                    <th>From</th>
                    <th>To</th>
                    <th>Type</th>
                    <th>Action</th>
                  </tr>
                </thead>
                <tbody>
                  {top3ClosestRides.map((ride) => (
                    <tr key={ride.ID}>
                      <td>{ride.FROM_LOCATION}</td>
                      <td>{ride.TO_LOCATION}</td>
                      <td>
                        <span className={`ride-type-badge ${ride.IS_SPECIAL_TASK ? 'special' : 'regular'}`}>
                          {ride.IS_SPECIAL_TASK ? 'Special Task Ride' : 'Regular Ride'}
                        </span>
                      </td>
                      <td>
                        <button
                          className="accept-btn"
                          onClick={() => handleAcceptRide(ride.ID)}
                          disabled={loading}
                        >
                          {loading ? 'Processing...' : 'Accept Ride'}
                        </button>
                      </td>
                    </tr>
                  ))}
                </tbody>
              </table>
            </div>
          ) : (
            <div className="no-rides-message">
              No closest rides available
            </div>
          )}

          <h3 className="section-title">Other Available Rides</h3>
          {otherAvailableRides.length > 0 ? (
            <>
              <div className="table-container">
                <table className="rides-table">
                  <thead>
                    <tr>
                      <th>From</th>
                      <th>To</th>
                      <th>Type</th>
                      <th>Action</th>
                    </tr>
                  </thead>
                  <tbody>
                    {currentRides.map((ride) => (
                      <tr key={ride.ID}>
                        <td>{ride.FROM_LOCATION}</td>
                        <td>{ride.TO_LOCATION}</td>
                        <td>
                          <span className={`ride-type-badge ${ride.IS_SPECIAL_TASK ? 'special' : 'regular'}`}>
                            {ride.IS_SPECIAL_TASK ? 'Special Task Ride' : 'Regular Ride'}
                          </span>
                        </td>
                        <td>
                          <button
                            className="accept-btn"
                            onClick={() => handleAcceptRide(ride.ID)}
                            disabled={loading}
                          >
                            {loading ? 'Processing...' : 'Accept Ride'}
                          </button>
                        </td>
                      </tr>
                    ))}
                  </tbody>
                </table>
              </div>
              <div className="pagination-controls">
                <button 
                  onClick={prevPage} 
                  disabled={currentPage === 1}
                  className="pagination-btn"
                >
                  &lt;
                </button>
                <span className="page-indicator">
                  Page {currentPage} of {totalPages}
                </span>
                <button 
                  onClick={nextPage} 
                  disabled={currentPage === totalPages}
                  className="pagination-btn"
                >
                  &gt;
                </button>
              </div>
            </>
          ) : (
            <div className="no-rides-message">
              No additional rides available
            </div>
          )}
        </div>
      )}
    </div>
  );
};

export default DriverDashboard;
