import React, { useState, useEffect } from "react";
import { useNavigate } from "react-router-dom";
import Confetti from "react-confetti";
import { bookRide } from "../Services/api";
import { io } from "socket.io-client";
import "./AvailableRides.css";

const SOCKET_URL = process.env.REACT_APP_SOCKET_URL || "http://localhost:5000";
const socket = io(SOCKET_URL, { autoConnect: false });

const AvailableRides = ({ user }) => {
  const navigate = useNavigate();
  const [fromLocation, setFromLocation] = useState("");
  const [toLocation, setToLocation] = useState("");
  const [estimatedTime, setEstimatedTime] = useState("");
  const [showConfirmation, setShowConfirmation] = useState(false);
  const [showConfetti, setShowConfetti] = useState(false);
  const [bookingSuccess, setBookingSuccess] = useState(false);
  const [currentUser, setCurrentUser] = useState(user || null);
  const [rideStatus, setRideStatus] = useState("");
  const [rideId, setRideId] = useState(null);
  const [isConnected, setIsConnected] = useState(false);

  const masterData = [
    { from: "SSD", to: "Boiler", time: 4 },
    { from: "SSD", to: "RMS", time: 10 },
    { from: "SSD", to: "Front gate", time: 5 },
    { from: "SSD", to: "Back gate", time: 8 },
    { from: "SSD", to: "Utility", time: 5 },
    { from: "RMS", to: "Front gate", time: 7 },
    { from: "RMS", to: "Back gate", time: 4 },
    { from: "RMS", to: "Boiler", time: 5 },
    { from: "RMS", to: "Utility", time: 4 },
  ];

  const uniqueFromLocations = [...new Set(masterData.map((data) => data.from))];
  const uniqueToLocations = [...new Set(masterData.map((data) => data.to))];

  useEffect(() => {
    let storedUser = localStorage.getItem("user");
    if (storedUser) {
      storedUser = JSON.parse(storedUser);
      setCurrentUser(storedUser);
    } else {
      console.warn("‚ö†Ô∏è No user found, using guest mode.");
      setCurrentUser({ id: Math.floor(Math.random() * 9000) + 1000 });
    }
  }, []);

  useEffect(() => {
    if (fromLocation && toLocation) {
      const ride = masterData.find(
        (data) => data.from === fromLocation && data.to === toLocation
      );
      setEstimatedTime(ride ? ride.time : "N/A");
    } else {
      setEstimatedTime("");
    }
  }, [fromLocation, toLocation]);

  useEffect(() => {
    if (!socket) return;
    
    socket.on("connect", () => setIsConnected(true));
    socket.on("disconnect", () => setIsConnected(false));

    const onRideAccepted = ({ rideId: acceptedRideId, driverId }) => {
      if (acceptedRideId === rideId) {
        setRideStatus("accepted");
        setShowConfetti(true);
        setBookingSuccess(true);
      }
    };

    socket.on("rideAccepted", onRideAccepted);

    return () => {
      socket.off("rideAccepted", onRideAccepted);
    };
  }, [rideId]);

  const handleConfirm = () => {
    if (!fromLocation || !toLocation) {
      alert("‚ùå Please select both From and To locations.");
      return;
    }
    setShowConfirmation(true);
  };

  const handleYes = async () => {
    if (!currentUser || !currentUser.id) {
      alert("‚ùå User not found. Using a temporary ID.");
      setCurrentUser({ id: 9999 });
    }

    const userId = Number(currentUser.id);
    if (isNaN(userId)) {
      console.error("‚ùå Invalid User ID:", currentUser.id);
      alert("‚ùå User ID is invalid. Please log in again.");
      return;
    }

    setShowConfirmation(false);
    setRideStatus("requested");

    try {
      console.log("üöÄ Booking ride for user:", userId);
      const response = await bookRide(userId, fromLocation, toLocation, "Forklift");

      if (response.rideId) {
        console.log("‚úÖ Ride booked successfully! Ride ID:", response.rideId);
        setRideId(response.rideId);
        socket.emit("rideRequested", { rideId: response.rideId });
      } else {
        console.error("‚ùå Failed to book ride.");
        alert("‚ùå Failed to book ride.");
      }
    } catch (error) {
      console.error("‚ùå API Error:", error);
      alert("‚ùå Error booking ride.");
    }
  };

  return (
    <div className="available-rides-container">
      {showConfetti && <Confetti numberOfPieces={400} gravity={0.2} />}
      <h2>Available Rides</h2>
      <p>Socket Connection: {isConnected ? "üü¢ Connected" : "üî¥ Disconnected"}</p>
      <label>From Location:</label>
      <select value={fromLocation} onChange={(e) => setFromLocation(e.target.value)}>
        <option value="" disabled>Select From</option>
        {uniqueFromLocations.map((loc, index) => (
          <option key={index} value={loc}>{loc}</option>
        ))}
      </select>
      <label>To Location:</label>
      <select value={toLocation} onChange={(e) => setToLocation(e.target.value)}>
        <option value="" disabled>Select To</option>
        {uniqueToLocations.map((loc, index) => (
          <option key={index} value={loc}>{loc}</option>
        ))}
      </select>
      {estimatedTime && <p>Your ride will take {estimatedTime} minutes.</p>}
      {!rideStatus && <button onClick={handleConfirm}>Confirm Ride</button>}
    </div>
  );
};

export default AvailableRides;
