import React, { useState, useEffect } from "react";
import { io } from "socket.io-client";
import "./DriverDashboard.css";
import { acceptRide, completeRide } from "../Services/api";

const SOCKET_URL = "http://localhost:5000";

const DriverDashboard = ({ driver }) => {
  const [ongoingRide, setOngoingRide] = useState(null);
  const [newRideRequests, setNewRideRequests] = useState([]);
  const [loading, setLoading] = useState(false);
  const [progress, setProgress] = useState(0);
  const [showEndRideConfirmation, setShowEndRideConfirmation] = useState(false);
  const [rideEndedMessage, setRideEndedMessage] = useState("");
  const [rideAcceptedMessage, setRideAcceptedMessage] = useState("");
  const [socket, setSocket] = useState(null);

  useEffect(() => {
    const newSocket = io(SOCKET_URL, {
      autoConnect: true,
      reconnection: true,
      reconnectionAttempts: 5,
      reconnectionDelay: 1000,
    });

    newSocket.on("connect", () => {
      console.log("Socket connected");
    });

    newSocket.on("connect_error", (err) => {
      console.error("Socket connection error:", err);
    });

    setSocket(newSocket);

    return () => {
      newSocket.off("connect");
      newSocket.off("connect_error");
      newSocket.disconnect();
    };
  }, []);

  const fetchNewRideRequests = async () => {
    try {
      const response = await fetch(`${SOCKET_URL}/api/drivers/available-rides`);
      if (!response.ok) {
        throw new Error("Failed to fetch driver data");
      }
      const data = await response.json();
      setNewRideRequests(data.rides.rows || []);
    } catch (error) {
      console.error("âŒ Error fetching driver data:", error);
    }
  };

  const handleAcceptRide = async (rideId) => {
    if (loading || !socket) return;
    setLoading(true);
    try {
      await acceptRide(driver.id, rideId);
      setRideAcceptedMessage("âœ… Ride accepted successfully!");
      const acceptedRide = newRideRequests.find((ride) => ride.ID === rideId);
      setOngoingRide(acceptedRide);
      setNewRideRequests((prev) => prev.filter((ride) => ride.ID !== rideId));
      socket.emit("rideAccepted", { rideId });
    } catch (error) {
      setRideAcceptedMessage("âŒ Failed to accept ride.");
      console.error("âŒ Error accepting ride:", error);
    }
    setLoading(false);
  };

  const handleEndRide = async () => {
    if (!ongoingRide || !socket) return;
    try {
      await completeRide(ongoingRide.ID);
      socket.emit("rideEnded", { rideId: ongoingRide.ID });
      setRideEndedMessage("âœ… Ride ended successfully!");
      setOngoingRide(null);
      setProgress(0);
    } catch (error) {
      console.error("âŒ Error ending ride:", error);
    }
  };

  useEffect(() => {
    if (ongoingRide) {
      const interval = setInterval(() => {
        setProgress((prev) => (prev >= 100 ? 100 : prev + 10));
      }, 1000);
      return () => clearInterval(interval);
    }
  }, [ongoingRide]);

  useEffect(() => {
    fetchNewRideRequests();
  }, []);

  return (
    <div className="driver-dashboard">
      <h2>Driver Dashboard</h2>
      {ongoingRide ? (
        <div className="ongoing-ride">
          <h3>Ongoing Ride</h3>
          <p>
            <strong>From:</strong> {ongoingRide.FROM_LOCATION} &nbsp;|&nbsp;
            <strong>To:</strong> {ongoingRide.TO_LOCATION}
          </p>
          <div className="progress-bar-container">
            <div className="progress-bar">
              <div className="progress-indicator" style={{ width: `${progress}%` }}></div>
              <div className="car-emoji" style={{ left: `${progress}%` }}>ðŸš—</div>
            </div>
          </div>
          {showEndRideConfirmation ? (
            <div className="end-ride-confirmation">
              <p>Are you sure you want to end the ride?</p>
              <button className="yes-button" onClick={handleEndRide}>Yes</button>
              <button className="no-button" onClick={() => setShowEndRideConfirmation(false)}>No</button>
            </div>
          ) : (
            <button className="end-ride-button" onClick={() => setShowEndRideConfirmation(true)}>End Ride</button>
          )}
          {rideEndedMessage && <p className="ride-ended-message">{rideEndedMessage}</p>}
        </div>
      ) : (
        <p className="no-rides">No ongoing ride.</p>
      )}
    </div>
  );
};

export default DriverDashboard;
