import express from "express";
import { executeQuery } from "./dbConfig.js";

const router = express.Router();

export default function driverRoutes(io) {
  // âœ… Mark Driver as HOME
  router.post("/mark-home", async (req, res) => {
    try {
      const { driverId } = req.body;

      if (!driverId) {
        return res.status(400).json({ error: "Driver ID is required" });
      }

      const result = await executeQuery(
        "UPDATE drivers SET status = 'HOME' WHERE driver_id = :driverId",
        { driverId }
      );

      if (result.rowsAffected === 0) {
        return res
          .status(404)
          .json({ error: "Driver not found or already HOME." });
      }

      // ðŸ”¹ Notify frontend about driver status change
      io.emit("driverStatusUpdated", { driverId, status: "HOME" });

      res.status(200).json({ message: "Driver marked as HOME." });
    } catch (error) {
      console.error("âŒ Error marking driver as HOME:", error);
      res.status(500).json({ error: "Failed to update driver status." });
    }
  });

  // âœ… Get Available Rides
  router.get("/available-rides", async (req, res) => {
    try {
      const query = `
        SELECT id, from_location, to_location, vehicle_type, ride_type, status
        FROM rides
        WHERE status = 'pending'
        ORDER BY created_at DESC
      `;

      const rides = await executeQuery(query);
      res.status(200).json({ rides });
    } catch (error) {
      console.error("âŒ Error fetching available rides:", error);
      res.status(500).json({ error: "Failed to fetch rides" });
    }
  });

  // âœ… Listen for new ride requests in WebSocket
  io.on("connection", (socket) => {
    socket.on("newRideRequest", (rideData) => {
      io.to("drivers").emit("newRideRequest", rideData);
    });
  });

  return router;
}
