import React, { useState, useEffect } from "react";
import { useNavigate } from "react-router-dom";
import Confetti from "react-confetti";
import { bookRide } from "../Services/api";
import { io } from "socket.io-client";
import "./AvailableRides.css";

const SOCKET_URL = "http://144.11.1.83:5000";

const AvailableRides = ({ user }) => {
  const navigate = useNavigate();
  const [fromLocation, setFromLocation] = useState("");
  const [toLocation, setToLocation] = useState("");
  const [estimatedTime, setEstimatedTime] = useState(null);
  const [showConfirmation, setShowConfirmation] = useState(false);
  const [showConfetti, setShowConfetti] = useState(false);
  const [bookingSuccess, setBookingSuccess] = useState(false);
  const [currentUser, setCurrentUser] = useState(user || null);
  const [rideStatus, setRideStatus] = useState("");
  const [rideId, setRideId] = useState(null);
  const [socket, setSocket] = useState(null);
  const [windowSize, setWindowSize] = useState({
    width: window.innerWidth,
    height: window.innerHeight,
  });
  const [rideProgress, setRideProgress] = useState(0);

  const masterData = [
    { from: "SSD", to: "Boiler", time: 4 },
    { from: "SSD", to: "RMS", time: 10 },
    { from: "SSD", to: "Front gate", time: 5 },
    { from: "SSD", to: "Back gate", time: 8 },
    { from: "SSD", to: "Utility", time: 5 },
    { from: "RMS", to: "Front gate", time: 7 },
    { from: "RMS", to: "Back gate", time: 4 },
    { from: "RMS", to: "Boiler", time: 5 },
    { from: "RMS", to: "Utility", time: 4 },
  ];

  const uniqueFromLocations = [...new Set(masterData.map((data) => data.from))];
  const uniqueToLocations = [...new Set(masterData.map((data) => data.to))];

  useEffect(() => {
    const handleResize = () => {
      setWindowSize({
        width: window.innerWidth,
        height: window.innerHeight,
      });
    };

    window.addEventListener("resize", handleResize);
    return () => window.removeEventListener("resize", handleResize);
  }, []);

  useEffect(() => {
    let confettiTimer;
    if (showConfetti) {
      confettiTimer = setTimeout(() => {
        setShowConfetti(false);
      }, 3000);
    }
    return () => {
      if (confettiTimer) clearTimeout(confettiTimer);
    };
  }, [showConfetti]);

  useEffect(() => {
    let storedUser = localStorage.getItem("user");
    if (storedUser) {
      storedUser = JSON.parse(storedUser);
      setCurrentUser(storedUser);
    } else {
      console.warn("âš ï¸ No user found, using guest mode.");
      setCurrentUser({ id: Math.floor(Math.random() * 9000) + 1000 });
    }
  }, []);

  useEffect(() => {
    if (fromLocation && toLocation) {
      const route = masterData.find(
        (route) => route.from === fromLocation && route.to === toLocation
      );
      if (route) {
        setEstimatedTime(route.time);
      } else {
        setEstimatedTime(null);
      }
    } else {
      setEstimatedTime(null);
    }
  }, [fromLocation, toLocation]);

  useEffect(() => {
    const newSocket = io(SOCKET_URL, { autoConnect: true });
    setSocket(newSocket);

    newSocket.on("connect", () => {
      console.log("âœ… Connected to available rides Socket.IO server");
    });

    newSocket.on("disconnect", () => {
      console.log(" Disconnected from available rides Socket.IO server");
    });

    newSocket.onAny((event, ...args) => {
      console.log(` Received event: ${event}`, args);
    });

    newSocket.on("rideAccepted", (data) => {
      const { rideId: acceptedRideId } = data;
      console.log("Received rideAccepted event with rideId:", acceptedRideId);

      if (acceptedRideId === rideId) {
        setRideStatus("accepted");
        setRideProgress(1);
        setShowConfetti(true);
        setBookingSuccess(true);
      }
    });

    newSocket.on("materialPicked", (data) => {
      if (data.rideId === rideId) {
        setRideProgress(2);
      }
    });

    newSocket.on("materialDropped", (data) => {
      if (data.rideId === rideId) {
        setRideProgress(3);
        setTimeout(() => {
          navigate("/requester");
        }, 5000);
      }
    });

    return () => {
      newSocket.disconnect();
      console.log(" Disconnected from Socket.IO server");
    };
  }, [rideId]);

  const handleConfirm = () => {
    if (!fromLocation || !toLocation) {
      alert(" Please select both From and To locations.");
      return;
    }
    setShowConfirmation(true);
  };

  const handleYes = async () => {
    if (!currentUser || !currentUser.id) {
      alert(" User not found. Using a temporary ID.");
      setCurrentUser({ id: 9999 });
    }

    const userId = Number(currentUser.id);
    if (isNaN(userId)) {
      console.error(" Invalid User ID:", currentUser.id);
      alert(" User ID is invalid. Please log in again.");
      return;
    }

    setShowConfirmation(false);
    setRideStatus("requested");
    setRideProgress(0);

    try {
      console.log("ðŸš€ Booking ride for user:", userId);
      const response = await bookRide(
        userId,
        fromLocation,
        toLocation,
        "Forklift",
        "Regular",
      );

      if (response.rideId) {
        console.log(" Ride booked successfully! Ride ID:", response.rideId);
        setRideId(response.rideId);
        socket.emit("rideRequested", { rideId: response.rideId });
      } else {
        console.error(" Failed to book ride.");
        alert(" Failed to book ride.");
        setRideStatus("");
      }
    } catch (error) {
      console.error(" API Error:", error);
      alert(" Error booking ride.");
      setRideStatus("");
    }
  };

  const handleNo = () => {
    setShowConfirmation(false);
  };

  const ProgressStep = ({ number, title, active, completed }) => {
    return (
      <div className="progress-step">
        <div
          className={`progress-circle ${active ? "active" : ""} ${
            completed ? "completed" : ""
          }`}
        >
          {completed ? (
            <svg
              xmlns="http://www.w3.org/2000/svg"
              width="16"
              height="16"
              viewBox="0 0 24 24"
              fill="none"
              stroke="currentColor"
              strokeWidth="2"
              strokeLinecap="round"
              strokeLinejoin="round"
            >
              <path d="M20 6L9 17l-5-5" />
            </svg>
          ) : (
            number
          )}
        </div>
        <div className={`progress-title ${active ? "active" : ""}`}>{title}</div>
      </div>
    );
  };

  const ProgressConnector = ({ active }) => {
    return (
      <div className={`progress-connector ${active ? "active" : ""}`}></div>
    );
  };

  return (
    <div className="available-rides-container">
      {showConfetti && (
        <Confetti
          width={windowSize.width}
          height={windowSize.height}
          numberOfPieces={400}
          gravity={0.2}
          recycle={false}
        />
      )}
      <h2>Available Rides</h2>

      {rideProgress > 0 && (
        <div className="ride-progress-tracker">
          <div className="progress-steps">
            <ProgressStep
              number={1}
              title="Requested"
              active={rideProgress === 0}
              completed={rideProgress > 0}
            />
            <ProgressConnector active={rideProgress > 0} />
            <ProgressStep
              number={2}
              title="Driver Assigned"
              active={rideProgress === 1}
              completed={rideProgress > 1}
            />
            <ProgressConnector active={rideProgress > 1} />
            <ProgressStep
              number={3}
              title="Material Picked"
              active={rideProgress === 2}
              completed={rideProgress > 2}
            />
            <ProgressConnector active={rideProgress > 2} />
            <ProgressStep
              number={4}
              title="Material Dropped"
              active={rideProgress === 3}
              completed={rideProgress > 3}
            />
          </div>
        </div>
      )}

      {rideProgress === 0 && (
        <>
          <div className="form-group">
            <label>From Location:</label>
            <div className="custom-select">
              <select
                value={fromLocation}
                onChange={(e) => setFromLocation(e.target.value)}
              >
                <option value="" disabled>
                  Select From
                </option>
                {uniqueFromLocations.map((loc, index) => (
                  <option key={index} value={loc}>
                    {loc}
                  </option>
                ))}
              </select>
              <span className="dropdown-arrow">&#9660;</span>
            </div>
          </div>

          <div className="form-group">
            <label>To Location:</label>
            <div className="custom-select">
              <select
                value={toLocation}
                onChange={(e) => setToLocation(e.target.value)}
              >
                <option value="" disabled>
                  Select To
                </option>
                {uniqueToLocations.map((loc, index) => (
                  <option key={index} value={loc}>
                    {loc}
                  </option>
                ))}
              </select>
              <span className="dropdown-arrow">&#9660;</span>
            </div>
          </div>

          {estimatedTime && (
            <div className="estimated-time">
              <p>
                Your ride will take <span className="highlight">{estimatedTime}</span>{" "}
                minutes
              </p>
            </div>
          )}
        </>
      )}

      {rideStatus === "requested" && rideProgress === 0 && (
        <div className="ride-status">
          <div className="loading-spinner"></div>
          <p>Waiting for driver to accept the ride...</p>
        </div>
      )}

      {rideProgress > 0 && (
        <div className="ride-status success">
          {rideProgress === 1 && (
            <>
              <svg
                xmlns="http://www.w3.org/2000/svg"
                width="24"
                height="24"
                viewBox="0 0 24 24"
                fill="none"
                stroke="currentColor"
                strokeWidth="2"
                strokeLinecap="round"
                strokeLinejoin="round"
              >
                <path d="M22 11.08V12a10 10 0 1 1-5.93-9.14"></path>
                <polyline points="22 4 12 14.01 9 11.01"></polyline>
              </svg>
              <p>Your ride is accepted. Driver is on the way.</p>
            </>
          )}
          {rideProgress === 2 && (
            <>
              <svg
                xmlns="http://www.w3.org/2000/svg"
                width="24"
                height="24"
                viewBox="0 0 24 24"
                fill="none"
                stroke="currentColor"
                strokeWidth="2"
                strokeLinecap="round"
                strokeLinejoin="round"
              >
                <circle cx="12" cy="12" r="10"></circle>
                <path d="M8 14s1.5 2 4 2 4-2 4-2"></path>
                <line x1="9" y1="9" x2="9.01" y2="9"></line>
                <line x1="15" y1="9" x2="15.01" y2="9"></line>
              </svg>
              <p>Material has been picked up by the driver.</p>
            </>
          )}
          {rideProgress === 3 && (
            <>
              <svg
                xmlns="http://www.w3.org/2000/svg"
                width="24"
                height="24"
                viewBox="0 0 24 24"
                fill="none"
                stroke="currentColor"
                strokeWidth="2"
                strokeLinecap="round"
                strokeLinejoin="round"
              >
                <path d="M22 11.08V12a10 10 0 1 1-5.93-9.14"></path>
                <polyline points="22 4 12 14.01 9 11.01"></polyline>
              </svg>
              <p>Material has been successfully delivered!</p>
              <p className="redirect-notice">Redirecting to requester page...</p>
            </>
          )}
        </div>
      )}

      {!rideStatus && (
        <button className="confirm-button" onClick={handleConfirm}>
          Confirm Ride
        </button>
      )}

      {showConfirmation && (
        <div className="confirmation-overlay">
          <div className="confirmation-box">
            <p>Are you sure you want to book this ride?</p>
            <div className="confirmation-buttons">
              <button className="yes-button" onClick={handleYes}>
                Yes
              </button>
              <button className="no-button" onClick={handleNo}>
                No
              </button>
            </div>
          </div>
        </div>
      )}
    </div>
  );
};

export default AvailableRides;
