import React, { useState, useEffect, useCallback } from "react";
import { io } from "socket.io-client";
import { useNavigate } from "react-router-dom";
import "./DriverDashboard.css";
import { acceptRide, completeRide } from "../Services/api";

const SOCKET_URL = "http://144.11.1.83:5000";

const DriverDashboard = ({ driver }) => {
  const [ongoingRide, setOngoingRide] = useState(null);
  const [newRideRequests, setNewRideRequests] = useState([]);
  const [completedRides, setCompletedRides] = useState([]);
  const [loading, setLoading] = useState(false);
  const [historyLoading, setHistoryLoading] = useState(false);
  const [progress, setProgress] = useState(0);
  const [showEndRideConfirmation, setShowEndRideConfirmation] = useState(false);
  const [rideEndedMessage, setRideEndedMessage] = useState("");
  const [rideAcceptedMessage, setRideAcceptedMessage] = useState("");
  const [socket, setSocket] = useState(null);
  const [activeTab, setActiveTab] = useState('available');
  const navigate = useNavigate();

  const MAX_RIDES_TO_SHOW = 10;

  const fetchNewRideRequests = useCallback(async () => {
    try {
      const response = await fetch(`${SOCKET_URL}/api/drivers/available-rides`);
      if (!response.ok) throw new Error("Failed to fetch driver data");
      const data = await response.json();
      setNewRideRequests(data.rides?.rows || []);
    } catch (error) {
      console.error("Error fetching driver data:", error);
    }
  }, []);

  const fetchCompletedRides = useCallback(async () => {
    setHistoryLoading(true);
    try {
      const response = await fetch(`${SOCKET_URL}/api/drivers/completed-rides?driverId=${driver.id}`);
      if (!response.ok) throw new Error("Failed to fetch ride history");
      const data = await response.json();
      setCompletedRides(data.rides || []);
    } catch (error) {
      console.error("Error fetching ride history:", error);
    } finally {
      setHistoryLoading(false);
    }
  }, [driver?.id]);

  useEffect(() => {
    const newSocket = io(SOCKET_URL, {
      autoConnect: true,
      reconnectionAttempts: 5,
      reconnectionDelay: 1000,
    });

    newSocket.on("connect", () => {
      newSocket.emit("getInitialRides", { driverId: driver?.id }, (rides) => {
        setNewRideRequests(rides);
      });
    });

    newSocket.on("newRideRequest", (newRide) => {
      setNewRideRequests(prev => {
        const exists = prev.some(ride => ride.ID === newRide.ID);
        return exists ? prev : [newRide, ...prev];
      });
    });

    newSocket.on("rideAccepted", ({ rideId }) => {
      if (ongoingRide?.ID === rideId) {
        setRideAcceptedMessage("Ride confirmed!");
        setTimeout(() => setRideAcceptedMessage(""), 3000);
      }
    });

    setSocket(newSocket);

    return () => {
      newSocket.disconnect();
    };
  }, [driver?.id]);

  useEffect(() => {
    fetchNewRideRequests();
    if (activeTab === 'history') {
      fetchCompletedRides();
    }
  }, [fetchNewRideRequests, fetchCompletedRides, activeTab]);

  const handleAcceptRide = async (rideId) => {
    if (loading || !socket) return;
    setLoading(true);
    
    try {
      const acceptedRide = newRideRequests.find(ride => ride.ID === rideId);
      if (!acceptedRide) throw new Error("Ride not found");
      
      await acceptRide(rideId, driver.id);
      setOngoingRide(acceptedRide);
      setNewRideRequests(prev => prev.filter(ride => ride.ID !== rideId));
      setRideAcceptedMessage("Ride accepted successfully!");
      
      socket.emit("rideAccepted", { 
        rideId,
        driverId: driver.id 
      });
    } catch (error) {
      setRideAcceptedMessage("Failed to accept ride.");
      console.error("Error accepting ride:", error);
    } finally {
      setLoading(false);
    }
  };

  const handleEndRide = async () => {
    console.log('1. Complete ride button clicked'); // Debug log
    if (!ongoingRide || !socket) {
      console.log('2. Missing ongoingRide or socket', { ongoingRide, socket }); // Debug log
      setRideEndedMessage("No active ride to complete");
      return;
    }
    
    try {
      console.log('3. Starting ride completion process for ride:', ongoingRide.ID); // Debug log
      setLoading(true);
      
      console.log('4. Calling completeRide API'); // Debug log
      const response = await completeRide(ongoingRide.ID);
      console.log('5. API Response:', response); // Debug log
      
      if (!response.success) {
        console.log('6. API returned failure'); // Debug log
        throw new Error(response.message || "Failed to end ride");
      }
      
      console.log('7. Updating local state with completed ride'); // Debug log
      const completedRideWithTimestamp = {
        ...ongoingRide,
        completed_at: response.ride?.end_time || new Date().toISOString(),
        status: 'completed'
      };
      
      setCompletedRides(prev => [completedRideWithTimestamp, ...prev]);
      setRideEndedMessage("Ride ended successfully!");
      
      console.log('8. Emitting rideEnded socket event'); // Debug log
      socket.emit("rideEnded", { 
        rideId: ongoingRide.ID,
        driverId: driver.id 
      });
  
      setTimeout(() => {
        console.log('9. Resetting UI after completion'); // Debug log
        setOngoingRide(null);
        setProgress(0);
        setRideEndedMessage("");
        fetchNewRideRequests();
        setActiveTab('history');
      }, 2000);
    } catch (error) {
      console.error('10. Error in handleEndRide:', error); // Debug log
      setRideEndedMessage(error.message || "Failed to end ride. Please try again.");
    } finally {
      console.log('11. Final cleanup'); // Debug log
      setLoading(false);
      setShowEndRideConfirmation(false);
    }
  };
  useEffect(() => {
    if (ongoingRide) {
      const interval = setInterval(() => {
        setProgress(prev => (prev >= 100 ? 100 : prev + 10));
      }, 1000);
      return () => clearInterval(interval);
    }
  }, [ongoingRide]);

  // Get first 10 rides for each tab
  const availableRidesToShow = newRideRequests.slice(0, MAX_RIDES_TO_SHOW);
  const completedRidesToShow = completedRides.slice(0, MAX_RIDES_TO_SHOW);

  return (
    <div className="driver-dashboard">
      <header className="dashboard-header">
        <h2>Welcome, {driver?.name}</h2>
        <div className="dashboard-tabs">
          <button 
            className={`tab-button ${activeTab === 'available' ? 'active' : ''}`}
            onClick={() => setActiveTab('available')}
          >
            Available Rides
          </button>
          <button 
            className={`tab-button ${activeTab === 'history' ? 'active' : ''}`}
            onClick={() => setActiveTab('history')}
          >
            Ride History
          </button>
        </div>
      </header>

      {ongoingRide && (
        <div className="ongoing-ride-container">
          <div className="ongoing-ride-card">
            <div className="ride-header">
              <h3>Current Ride in Progress</h3>
              <span className="ride-type-badge">
                {ongoingRide.rideType || 'Regular'}
              </span>
            </div>
            
            <div className="ride-details">
              <div className="ride-detail-item">
                <span className="detail-label">From:</span>
                <span className="detail-value">{ongoingRide.FROM_LOCATION}</span>
              </div>
              <div className="ride-detail-item">
                <span className="detail-label">To:</span>
                <span className="detail-value">{ongoingRide.TO_LOCATION}</span>
              </div>
            </div>

            <div className="progress-container">
              <div className="progress-labels">
                <span>Pickup</span>
                <span>Destination</span>
              </div>
              <div className="progress-bar">
                <div className="progress-fill" style={{ width: `${progress}%` }}></div>
                <div className="progress-car" style={{ left: `${progress}%` }}>ðŸš—</div>
              </div>
            </div>

            {showEndRideConfirmation ? (
              <div className="confirmation-modal">
                <p>Confirm ride completion?</p>
                <div className="confirmation-actions">
                  <button className="confirm-btn" onClick={handleEndRide}>
                    Complete Ride
                  </button>
                  <button 
                    className="cancel-btn" 
                    onClick={() => setShowEndRideConfirmation(false)}
                  >
                    Cancel
                  </button>
                </div>
              </div>
            ) : (
              <button 
                className="end-ride-btn"
                onClick={() => 
                  setShowEndRideConfirmation(true)}
              >
                Complete Ride
              </button>
            )}

            {rideEndedMessage && (
              <div className={`status-message ${rideEndedMessage.includes("success") ? "success" : "error"}`}>
                {rideEndedMessage}
              </div>
            )}
          </div>
        </div>
      )}

      {rideAcceptedMessage && (
        <div className={`status-message ${rideAcceptedMessage.includes("success") ? "success" : "error"}`}>
          {rideAcceptedMessage}
        </div>
      )}

      {activeTab === 'available' ? (
        <div className="available-rides-section">
          <h3 className="section-title">Available Rides</h3>
          {availableRidesToShow.length > 0 ? (
            <div className="table-container">
              <div className="table-scroll-wrapper">
                <table className="rides-table">
                  <thead>
                    <tr>
                      <th>Pickup</th>
                      <th>Destination</th>
                      <th>Type</th>
                      <th>Action</th>
                    </tr>
                  </thead>
                  <tbody>
                    {availableRidesToShow.map(ride => (
                      <tr key={ride.ID}>
                        <td>{ride.FROM_LOCATION}</td>
                        <td>{ride.TO_LOCATION}</td>
                        <td>
                          <span className={`ride-type ${ride.rideType?.toLowerCase() || 'regular'}`}>
                            {ride.rideType || 'Regular'}
                          </span>
                        </td>
                        <td>
                          <button
                            className="accept-btn"
                            onClick={() => handleAcceptRide(ride.ID)}
                            disabled={loading}
                          >
                            {loading ? 'Processing...' : 'Accept'}
                          </button>
                        </td>
                      </tr>
                    ))}
                  </tbody>
                </table>
              </div>
            </div>
          ) : (
            <div className="no-rides-message">
              Currently no rides available
            </div>
          )}
        </div>
      ) : (
        <div className="history-section">
          <h3 className="section-title">Your Ride History</h3>
          {historyLoading ? (
            <div className="loading-message">Loading your ride history...</div>
          ) : completedRidesToShow.length > 0 ? (
            <div className="table-container">
              <div className="table-scroll-wrapper">
              <table className="rides-table">
  <thead>
    <tr>
      <th>Date</th>
      <th>From</th>
      <th>To</th>
      <th>Type</th>
      <th>Status</th>
    </tr>
  </thead>
  <tbody>
    {completedRidesToShow.map(ride => (
      <tr key={ride.ID}>
        <td>{new Date(ride.end_time || ride.created_at).toLocaleString()}</td>
        <td>{ride.FROM_LOCATION}</td>
        <td>{ride.TO_LOCATION}</td>
        <td>
          <span className={`ride-type ${ride.rideType?.toLowerCase() || 'regular'}`}>
            {ride.rideType || 'Regular'}
          </span>
        </td>
        <td>
          <span className="status-badge completed">Completed</span>
        </td>
      </tr>
    ))}
  </tbody>
</table>
              </div>
            </div>
          ) : (
            <div className="no-rides-message">
              You haven't completed any rides yet
            </div>
          )}
        </div>
      )}
    </div>
  );
};

export default DriverDashboard;
