import React, { useState, useEffect } from "react";
import { io } from "socket.io-client";
import "./DriverDashboard.css";
import { acceptRide, completeRide } from "../Services/api";

const SOCKET_URL = "http://144.11.1.83:5000";

const DriverDashboard = ({ driver }) => {
  const [ongoingRide, setOngoingRide] = useState(null);
  const [newRideRequests, setNewRideRequests] = useState([]);
  const [loading, setLoading] = useState(false);
  const [progress, setProgress] = useState(0);
  const [showEndRideConfirmation, setShowEndRideConfirmation] = useState(false);
  const [rideEndedMessage, setRideEndedMessage] = useState("");
  const [rideAcceptedMessage, setRideAcceptedMessage] = useState("");
  const [socket, setSocket] = useState(null); // Store socket instance in state

  // Initialize Socket.IO connection
  useEffect(() => {
    const newSocket = io(SOCKET_URL, { autoConnect: true }); // Auto-connect the socket
    setSocket(newSocket);

    newSocket.on("connect", () => {
      console.log("âœ… Connected to DriverDashboard Socket.IO server");
    });

    newSocket.on("disconnect", () => {
      console.log("âŒ Disconnected from DriverDashboard Socket.IO server");
    });

    // Listen for new ride requests added in real-time
    newSocket.on("newRideRequest", (newRide) => {
      console.log("ðŸ†• New ride received:", newRide);

      setNewRideRequests((prevRides) => [newRide, ...prevRides]); // Add new ride to the top
    });

    // Cleanup on unmount
    return () => {
      newSocket.disconnect();
    };
  }, []); // Empty dependency array ensures this runs only once on mount

  // Fetch new ride requests
  const fetchNewRideRequests = async () => {
    try {
      const response = await fetch(
        `http://172.20.10.4:5000/api/drivers/available-rides`
      );
      if (!response.ok) {
        throw new Error("Failed to fetch driver data");
      }
      const data = await response.json();
      const rides = data.rides.rows || [];

      console.log("rides = ", rides);
      setNewRideRequests(rides);
    } catch (error) {
      console.error("âŒ Error fetching driver data:", error);
    }
  };

  // Handle accepting a ride
  const handleAcceptRide = async (rideId, driverID) => {
    if (loading) return;

    setLoading(true);
    try {
      setRideAcceptedMessage("âœ… Ride accepted successfully!");

      // Move the ride to "Ongoing Ride"
      const acceptedRide = newRideRequests.find((ride) => ride.ID === rideId);
      console.log("ðŸš— Accepted Ride:", acceptedRide);

      setOngoingRide(acceptedRide);
      setNewRideRequests((prev) => prev.filter((ride) => ride.ID !== rideId));

      console.log("Emitting rideAccepted for rideId:", rideId);

      if (socket) {
        socket.emit("rideAccepted", { rideId }); // Emit rideAccepted event
      } else {
        console.error("âŒ Socket not initialized. Cannot emit event.");
      }
    } catch (error) {
      setRideAcceptedMessage("âŒ Failed to accept ride.");
      console.error("âŒ Error accepting ride:", error);
    }
    setLoading(false);
  };

  // Handle ending a ride
  const handleEndRide = async () => {
    if (!ongoingRide || !socket) return;

    try {
      console.log("Ongoing Ride Object:", ongoingRide); // Log the ongoingRide object
      console.log("Ending ride with ID:", ongoingRide.Id); // Log the Id (not rideId)

      const response = await completeRide(ongoingRide.Id); // Use ongoingRide.Id
      setRideEndedMessage("âœ… Ride ended successfully!");

      // Notify the requester
      socket.emit("rideEnded", { rideId: ongoingRide.Id }); // Use ongoingRide.Id

      // Clear the ongoing ride and progress
      setTimeout(() => {
        setOngoingRide(null);
        setProgress(0);
        setRideEndedMessage("");
        fetchNewRideRequests(); // Fetch new rides after ending the ride
      }, 2000);
    } catch (error) {
      setRideEndedMessage("âŒ Failed to end ride.");
      console.error("âŒ Error ending ride:", error);
    }
  };

  // Simulate ride progress
  useEffect(() => {
    if (ongoingRide) {
      const interval = setInterval(() => {
        setProgress((prev) => {
          if (prev >= 100) {
            clearInterval(interval);
            return 100;
          }
          return prev + 10; // Increment progress by 10% every second
        });
      }, 1000);

      return () => clearInterval(interval);
    }
  }, [ongoingRide]);

  // Fetch new ride requests on mount
  useEffect(() => {
    fetchNewRideRequests();
  }, [newRideRequests]);

  // Separate rides into "Top 3 Closest Rides" and "Other Available Rides"
  const top3ClosestRides = newRideRequests.slice(0, 3);
  const otherAvailableRides = newRideRequests.slice(3, 13);

  return (
    <div className="driver-dashboard">
      <h2>Driver Dashboard</h2>

      {/* Ongoing Ride Section */}
      {ongoingRide ? (
        <div className="ongoing-ride">
          <h3>Ongoing Ride</h3>
          <p>
            <strong>From:</strong> {ongoingRide.FROM_LOCATION} &nbsp;|&nbsp;
            <strong>To:</strong> {ongoingRide.TO_LOCATION}
          </p>
          <div className="progress-bar-container">
            <div className="progress-bar">
              <div
                className="progress-indicator"
                style={{ width: `${progress}%` }}
              ></div>
              <div className="car-emoji" style={{ left: `${progress}%` }}>
                ðŸš—
              </div>
            </div>
          </div>
          {showEndRideConfirmation ? (
            <div className="end-ride-confirmation">
              <p>Are you sure you want to end the ride?</p>
              <div className="confirmation-buttons">
                <button className="yes-button" onClick={handleEndRide}>
                  Yes
                </button>
                <button
                  className="no-button"
                  onClick={() => setShowEndRideConfirmation(false)}
                >
                  No
                </button>
              </div>
            </div>
          ) : (
            <button
              className="end-ride-button"
              onClick={() => setShowEndRideConfirmation(true)}
            >
              End Ride
            </button>
          )}
          {rideEndedMessage && (
            <p className="ride-ended-message">{rideEndedMessage}</p>
          )}
        </div>
      ) : (
        <p className="no-rides">No ongoing ride.</p>
      )}

      {/* Ride Accepted Message */}
      {rideAcceptedMessage && (
        <p className="ride-accepted-message">{rideAcceptedMessage}</p>
      )}

      {/* Show rides only if there's no ongoing ride */}
      {!ongoingRide && (
        <>
          {/* Top 3 Closest Rides Section */}
          <h3>Top 3 Closest Rides</h3>
          {top3ClosestRides.length === 0 ? (
            <p className="no-rides">No closest rides available.</p>
          ) : (
            <table className="rides-table">
              <thead>
                <tr>
                  <th>From</th>
                  <th>To</th>
                  <th>Action</th>
                </tr>
              </thead>
              <tbody>
                {top3ClosestRides.map((ride) => (
                  <tr key={ride.ID}>
                    <td>{ride.FROM_LOCATION}</td>
                    <td>{ride.TO_LOCATION}</td>
                    <td>
                      <button
                        className="accept-ride"
                        onClick={() =>
                          handleAcceptRide(ride.ID, ride.DRIVER_ID)
                        }
                        disabled={loading}
                      >
                        {loading ? "Processing..." : "Accept Ride"}
                      </button>
                    </td>
                  </tr>
                ))}
              </tbody>
            </table>
          )}

          {/* Other Available Rides Section */}
          <h3>Other Available Rides</h3>
          {otherAvailableRides.length === 0 ? (
            <p className="no-rides">No additional rides available.</p>
          ) : (
            <table className="rides-table">
              <thead>
                <tr>
                  <th>From</th>
                  <th>To</th>
                  <th>Action</th>
                </tr>
              </thead>
              <tbody>
                {otherAvailableRides.map((ride) => (
                  <tr key={ride.ID}>
                    <td>{ride.FROM_LOCATION}</td>
                    <td>{ride.TO_LOCATION}</td>
                    <td>
                      <button
                        className="accept-ride"
                        onClick={() =>
                          handleAcceptRide(ride.ID, ride.DRIVER_ID)
                        }
                        disabled={loading}
                      >
                        {loading ? "Processing..." : "Accept Ride"}
                      </button>
                    </td>
                  </tr>
                ))}
              </tbody>
            </table>
          )}
        </>
      )}
    </div>
  );
};

