import React, { useState, useEffect } from "react";
import axios from "axios";
import "./Requests.css";

const Requests = () => {
  const [requests, setRequests] = useState([]);

  // Fetch data initially and every 5 seconds (Polling)
  useEffect(() => {
    fetchRequests(); // Fetch once on component load
    const interval = setInterval(fetchRequests, 5000); // Poll every 5 seconds
    return () => clearInterval(interval); // Cleanup on unmount
  }, []);

  const fetchRequests = async () => {
    try {
      const response = await axios.get("http://144.11.1.83:5000/api/requests"); // Replace with your backend API
      setRequests(response.data);
    } catch (error) {
      console.error("Error fetching requests:", error);
    }
  };

  return (
    <div className="requests-container">
      <h2>Requests</h2>
      <table>
        <thead>
          <tr>
            <th>Sr No.</th>
            <th>Vehicle Number</th>
            <th>Name</th>
            <th>Mobile Number</th>
            <th>Role</th>
            <th>Status</th>
            <th>Forklifts Allocated</th>
            <th>Comment</th>
          </tr>
        </thead>
        <tbody>
          {requests.length === 0 ? (
            <tr>
              <td colSpan="8">No requests available</td>
            </tr>
          ) : (
            requests.map((request, index) => (
              <tr key={request.id}>
                <td>{index + 1}</td>
                <td>{request.vehicleNumber || "N/A"}</td>
                <td>{request.name || "N/A"}</td>
                <td>{request.mobileNumber || "N/A"}</td>
                <td>{request.role || "N/A"}</td>
                <td>{request.status || "N/A"}</td>
                <td>{request.role === "Driver" ? request.forklifts || 0 : "-"}</td>
                <td>{request.comment || "N/A"}</td>
              </tr>
            ))
          )}
        </tbody>
      </table>
    </div>
  );
};

export default Requests;
